<?php/** * Custom Walker * * @access      public * @since       1.0  * @return      void*/class wp_svg_icon_nav_walker extends Walker_Nav_Menu{      function start_el( &$output, $item, $depth = 0, $args = array(), $id = 0 )      {           global $wp_query;           $indent = ( $depth ) ? str_repeat( "\t", $depth ) : '';					   // Grab our stored menu Icon		   $item->subtitle = get_post_meta( $item->ID, '_menu_item_icon', true );			           $class_names = $value = '';           $classes = empty( $item->classes ) ? array() : (array) $item->classes;           $class_names = join( ' ', apply_filters( 'nav_menu_css_class', array_filter( $classes ), $item ) );           $class_names = ' class="'. esc_attr( $class_names ) . '"';           $output .= $indent . '<li id="menu-item-'. $item->ID . '"' . $value . $class_names .'>';           $attributes  = ! empty( $item->attr_title ) ? ' title="'  . esc_attr( $item->attr_title ) .'"' : '';           $attributes .= ! empty( $item->target )     ? ' target="' . esc_attr( $item->target     ) .'"' : '';           $attributes .= ! empty( $item->xfn )        ? ' rel="'    . esc_attr( $item->xfn        ) .'"' : '';           $attributes .= ! empty( $item->url )        ? ' href="'   . esc_attr( $item->url        ) .'"' : '';           $prepend = '';           $append = '';           $description  = ! empty( $item->description ) ? '<span>'.esc_attr( $item->description ).'</span>' : '';           if($depth != 0)           {	           $description = $append = $prepend = "";           }						// check if its a custom , or a default icon			if( $item->subtitle == 'no icon' || $item->subtitle == '' ) {				$icon = '';			} else {				if (strpos(  $item->subtitle, 'wp-svg-' ) !== false) {					$class = '';					$icon = '<span class="'. $class . $item->subtitle.' wp-svg-menu-icon">';				} else {					$class = 'wp-svg-custom-';					$icon = '<span class="'. $class . $item->subtitle.' wp-svg-menu-icon">';				}			}		   		   if( isset( $args->before ) ) {				$item_output = $args->before;				$item_output .= '<a'. $attributes .'>' . $icon;				if( $item->subtitle != 'no icon' || $item->subtitle != '' ) {					$item_output .= '</span> ';				}				$item_output .= $args->link_before .$prepend.apply_filters( 'the_title', $item->title, $item->ID ).$append;				$item_output .= $description.$args->link_after;				$item_output .= '</a>';				$item_output .= $args->after;			} else {				$item_output = '';			}            $output .= apply_filters( 'walker_nav_menu_start_el', $item_output, $item, $depth, $args );		}}